import "./App.css";
import { useState } from 'react'

function Square() {
  return <div className="chess">Hee</div>;
};

function Row(){
  let row = [];
  for(let i = 0; i < 8; i++){
    row.push(<Square />)
  };
  return row;
}

function App() {
  // const [board, setBoard] = useState(Array(8).fill(Array(8).fill(null)));
  const renderRows = () => {
    let rows = [];
    for(let i = 0; i < 8; i++){
      rows.push(<Row />)
    };
    return rows;
  }

  return (
    <div className="board">
      {/* <Row /> */}
      <div></div>
      {renderRows()}
  </div>
  );
};

export default App;
