import "./App.css";
import { useState } from 'react';


function Square() {
  return <div className="chess">Hee</div>;
};

function Row() {
  const squares = [];
  for (let i = 0; i < 8; i++) {
    squares.push(<Square />);
  };
  // console.log(squares, 'squares');
  return squares;
};

function App() {
  const [board, setBoard] = useState(Array(8).fill(Array(8).fill(null)));
  console.log(board, 'its board');
  const renderRows = () => {
    let rows = [];
    let eachLine = [];
    for(let i = 0; i < 8; i++){
      for(let j = 0; j < 8; j++){
        eachLine.push(<Row />);
      }
      rows.push(eachLine);
    };
    console.log(rows, 'rows');
    return rows;
  };

  return <div className="App">{renderRows()}</div>;
};

export default App;
