import React, {useState, useEffect} from 'react'

const top = {
    display: 'flex',
    marginTop: '100px',
};

const mainText = {
    flex: 2,
};

const image = {
    opacity: .1,
    flex: 1,
};

const bottom = {
    marginTop: '50px'
};

const box1 = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
};

const box2 = {
};

const buttonStyle = {
    borderRadius: '18px',
    backgroundColor: 'bisque',
    borderStyle: 'dashed',
};

const inputStyle = {
    backgroundColor: 'bisque',
    borderRadius: '18px',
    borderStyle: 'dashed',
    marginRight: '15px',
    width: '250px'
};

const Content = () => {
    const [link, setLink] = useState('https://www.shutterstock.com/image-vector/girl-working-on-computer-evening-260nw-773613916.jpg');
    const [linkArr, setLinkArr] = useState([]);
    const [validLink, setValidLink] = useState(true);

    const shortenLink = () => {
        if(link.trim()){
        fetch(`https://api.shrtco.de/v2/shorten?url=${link}`)
        .then((response) => response.json())
        .then((data) => 
        {
            console.log(data, 'its data');
        if(!data?.ok) setValidLink(false)
        else{
            setValidLink(false)
        setLinkArr((prev) => [
            ...prev,
            {
                originalLink: link,
                shortenedLink: data.result.short_link,
            }
            
        ])}});
        setLink('');
      };
    };

    console.log(linkArr, 'its linkArr');

  return (
    <div>
        <div style={top}>
        <div style={mainText}>More than just shortner links</div>
        <div style={image}>
            <img src='https://www.shutterstock.com/image-vector/girl-working-on-computer-evening-260nw-773613916.jpg'/>
        </div>
        </div>

        <div style={bottom}>
            <div style={box1}>
                <input 
                value={link}
                style={inputStyle} 
                onChange={(e) => setLink(e.target.value)}
                type='url' 
                />
                <button 
                style={buttonStyle}
                onClick={shortenLink}
                > 
                Shorten
                </button>
            </div>
            {
                !linkArr.length ? "No Url" :
                <div style={box1}>
                    
                    {
                        linkArr.map((elem) => {
                            return(
                                <>
                                <div>{elem.originalLink}</div>
                                <div>{elem.shortenedLink}</div>
                                </>
                            )
                        })
                    }
                </div> 
            }
        </div>
    </div>
  )
}

export default Content;
