import React, {useState, useEffect} from 'react'

const top = {
    display: 'flex',
    marginTop: '100px',
};

const mainText = {
    flex: 2,
};

const image = {
    opacity: .1,
    flex: 1,
};

const bottom = {
    marginTop: '50px'
};

const box1 = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
};

const box2 = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    flexDirection: 'column'
};

const buttonStyle = {
    borderRadius: '18px',
    backgroundColor: 'bisque',
    borderStyle: 'dashed',
};

const inputStyle = {
    backgroundColor: 'bisque',
    borderRadius: '18px',
    borderStyle: 'dashed',
    marginRight: '15px',
    width: '250px'
};

const row = {
    
}

const Content = () => {
    const [link, setLink] = useState('https://www.shutterstock.com/image-vector/girl-working-on-computer-evening-260nw-773613916.jpg');
    const [linkArr, setLinkArr] = useState([]);
    const [validLink, setValidLink] = useState(true);

    const shortenLink = async () => {
        if(link.trim()){
        const response = await fetch(`https://api.shrtco.de/v2/shorten?url=${link}`);
        const data = await response.json();
        if(!data?.ok) setValidLink(false);
        else{
        setValidLink(true);
        setLinkArr((prev) => [
            ...prev,
            {
                originalLink: link,
                shortenedLink: data.result.short_link,
            }
            
        ])};
        setLink('');
      } else {
        setValidLink(false);
      }
    };

    console.log(linkArr, 'its linkArr');

  return (
    <div>
        <div style={top}>
        <div style={mainText}>More than just shortner links</div>
        <div style={image}>
            <img src='https://www.shutterstock.com/image-vector/girl-working-on-computer-evening-260nw-773613916.jpg'/>
        </div>
        </div>

        <div style={bottom}>
            <div style={box1}>
                <input 
                value={link}
                style={inputStyle} 
                onChange={(e) => setLink(e.target.value)}
                type='url' 
                />
                <button 
                style={buttonStyle}
                onClick={shortenLink}
                > 
                Shorten
                </button>
            </div>
            {!validLink ?
            "Entered Url is not correct" : 
            (
                !linkArr.length ? "No Url" :
                <div style={box2}>
                    
                    {
                        linkArr.map((elem) => {
                            return(
                                <div style={row}>
                                {elem.originalLink}
                                {elem.shortenedLink}
                                </div>
                            )
                        })
                    }
                </div> 
            )}
        </div>
    </div>
  )
}

export default Content;
